import React, { useState, useEffect } from 'react';
import { Toaster, toast } from 'react-hot-toast';
import { Trophy, Upload, Shuffle, Users, Zap, RefreshCw } from 'lucide-react';
import axios from 'axios';
import UploadSection from './components/UploadSection';
import GameSection from './components/GameSection';
import StatsSection from './components/StatsSection';

const API_BASE = 'http://localhost:8000';

function App() {
  const [players, setPlayers] = useState([]);
  const [gameState, setGameState] = useState('loading'); // 'loading', 'upload', 'playing'
  const [currentMashup, setCurrentMashup] = useState(null);
  const [quizOptions, setQuizOptions] = useState([]);
  const [correctAnswer, setCorrectAnswer] = useState('');
  const [score, setScore] = useState(0);
  const [totalQuestions, setTotalQuestions] = useState(0);
  const [isLoading, setIsLoading] = useState(false);
  const [gameMode, setGameMode] = useState('answered'); // 'playing', 'answered'
  const [backendStatus, setBackendStatus] = useState('connecting');

  const [stats, setStats] = useState({
    accuracy: 0,
    bestStreak: 0,
    currentStreak: 0,
    avgTime: 0
  });

  // Check for pre-loaded players on startup
  useEffect(() => {
    checkBackendAndLoadPlayers();
  }, []);

  const checkBackendAndLoadPlayers = async () => {
    try {
      setBackendStatus('connecting');
      setGameState('loading');

      // Check backend status
      const statusResponse = await axios.get(`${API_BASE}/`);
      setBackendStatus('connected');

      // Load existing players
      const playersResponse = await axios.get(`${API_BASE}/players`);
      
      if (playersResponse.data.players && playersResponse.data.players.length > 0) {
        setPlayers(playersResponse.data.players);
        setGameState(playersResponse.data.ready_to_play ? 'ready' : 'upload');
        toast.success(`🎯 Auto-loaded ${playersResponse.data.players.length} cricket players!`, {
          icon: '🏏',
        });
      } else {
        setGameState('upload');
        toast.info('📁 No images found in uploads folder. Add images to D:\\Swapnil\\Hackathon-2024\\cricket-face-mashup-web\\uploads\\', {
          duration: 6000,
        });
      }
    } catch (error) {
      console.error('Backend connection error:', error);
      setBackendStatus('error');
      setGameState('upload');
      toast.error('❌ Backend connection failed. Make sure the server is running.', {
        duration: 6000,
      });
    }
  };

  const reloadImages = async () => {
    try {
      setIsLoading(true);
      const response = await axios.post(`${API_BASE}/reload-images`);
      
      if (response.data.success) {
        const playersResponse = await axios.get(`${API_BASE}/players`);
        setPlayers(playersResponse.data.players);
        setGameState(playersResponse.data.ready_to_play ? 'ready' : 'upload');
        toast.success(`🔄 Reloaded ${response.data.total_players} players!`);
      }
    } catch (error) {
      console.error('Error reloading images:', error);
      toast.error('Failed to reload images.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleUploadComplete = (uploadedPlayers) => {
    setPlayers(uploadedPlayers);
    setGameState('ready');
    toast.success(`Uploaded ${uploadedPlayers.length} players! Ready to play!`, {
      icon: '🏏',
    });
  };

  const startNewRound = async () => {
    if (players.length < 3) {
      toast.error('Need at least 3 players to start the game!');
      return;
    }

    setIsLoading(true);
    setGameMode('playing');

    try {
      // Create mashup
      const mashupResponse = await axios.post(`${API_BASE}/create-mashup`, null, {
        params: { num_players: 3 }
      });

      if (mashupResponse.data.success) {
        setCurrentMashup(mashupResponse.data);

        // Generate quiz options
        const quizResponse = await axios.post(`${API_BASE}/generate-quiz`, {
          correct_players: mashupResponse.data.used_players,
          total_options: 4
        });

        if (quizResponse.data.success) {
          setQuizOptions(quizResponse.data.options);
          setCorrectAnswer(quizResponse.data.correct_answer);
          setGameState('playing');
          toast.success('New mashup created! Can you guess the players?', {
            icon: '🎯',
          });
        }
      }
    } catch (error) {
      console.error('Error creating mashup:', error);
      toast.error('Failed to create mashup. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleAnswer = (selectedAnswer) => {
    const isCorrect = selectedAnswer === correctAnswer;
    setTotalQuestions(prev => prev + 1);
    
    if (isCorrect) {
      setScore(prev => prev + 1);
      setStats(prev => ({
        ...prev,
        currentStreak: prev.currentStreak + 1,
        bestStreak: Math.max(prev.bestStreak, prev.currentStreak + 1)
      }));
      toast.success('Correct! Well done! 🎉', {
        icon: '✅',
      });
    } else {
      setStats(prev => ({
        ...prev,
        currentStreak: 0
      }));
      toast.error(`Wrong! Correct answer: ${correctAnswer}`, {
        icon: '❌',
      });
    }

    // Update accuracy
    const newAccuracy = totalQuestions > 0 ? 
      Math.round(((isCorrect ? score + 1 : score) / (totalQuestions + 1)) * 100) : 0;
    setStats(prev => ({ ...prev, accuracy: newAccuracy }));

    setGameMode('answered');
  };

  const clearAllPlayers = async () => {
    try {
      await axios.delete(`${API_BASE}/clear-players`);
      setPlayers([]);
      setGameState('upload');
      setCurrentMashup(null);
      setScore(0);
      setTotalQuestions(0);
      setStats({ accuracy: 0, bestStreak: 0, currentStreak: 0, avgTime: 0 });
      toast.success('All players cleared from memory!');
    } catch (error) {
      console.error('Error clearing players:', error);
      toast.error('Failed to clear players.');
    }
  };

  if (gameState === 'loading') {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 via-indigo-50 to-purple-50 flex items-center justify-center">
        <div className="text-center space-y-4">
          <div className="w-16 h-16 border-4 border-blue-200 border-t-blue-500 rounded-full animate-spin mx-auto"></div>
          <h2 className="text-2xl font-bold text-gray-800">Loading Cricket Face Mashup</h2>
          <p className="text-gray-600">
            {backendStatus === 'connecting' ? 'Connecting to server...' : 
             backendStatus === 'connected' ? 'Loading players...' : 
             'Checking for images...'}
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-indigo-50 to-purple-50">
      <Toaster 
        position="top-right"
        toastOptions={{
          duration: 4000,
          style: {
            background: '#ffffff',
            color: '#374151',
            borderRadius: '12px',
            boxShadow: '0 10px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04)',
          },
        }}
      />
      
      {/* Header */}
      <header className="bg-white shadow-lg border-b-4 border-cricket-blue">
        <div className="max-w-7xl mx-auto px-4 py-6">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <div className="p-3 bg-gradient-to-r from-green-500 to-blue-500 rounded-xl shadow-lg">
                <Trophy className="h-8 w-8 text-white" />
              </div>
              <div>
                <h1 className="text-3xl font-bold bg-gradient-to-r from-green-600 to-blue-600 bg-clip-text text-transparent">
                  Cricket Face Mashup Challenge
                </h1>
                <p className="text-gray-600 mt-1">Guess the blended cricket legends!</p>
              </div>
            </div>
            
            <div className="flex items-center space-x-4">
              {players.length > 0 && (
                <>
                  <div className="flex items-center space-x-2 text-sm text-gray-600">
                    <Users className="h-4 w-4" />
                    <span>{players.length} Players</span>
                  </div>
                  <button
                    onClick={reloadImages}
                    disabled={isLoading}
                    className="px-3 py-2 text-sm bg-blue-500 hover:bg-blue-600 text-white rounded-lg transition-colors duration-200 flex items-center space-x-1"
                  >
                    <RefreshCw className={`h-4 w-4 ${isLoading ? 'animate-spin' : ''}`} />
                    <span>Reload</span>
                  </button>
                  <button
                    onClick={clearAllPlayers}
                    className="px-3 py-2 text-sm bg-red-500 hover:bg-red-600 text-white rounded-lg transition-colors duration-200"
                  >
                    Clear
                  </button>
                </>
              )}
            </div>
          </div>
        </div>
      </header>

      <main className="max-w-7xl mx-auto px-4 py-8">
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Main Content */}
          <div className="lg:col-span-2 space-y-6">
            {gameState === 'upload' ? (
              <div className="space-y-6">
                <UploadSection onUploadComplete={handleUploadComplete} />
                
                {/* Instructions for folder upload */}
                <div className="card p-6 bg-blue-50 border-blue-200">
                  <div className="space-y-3">
                    <h3 className="text-lg font-semibold text-blue-800 flex items-center space-x-2">
                      <Upload className="h-5 w-5" />
                      <span>Quick Setup: Use Folder Upload</span>
                    </h3>
                    <div className="text-sm text-blue-700 space-y-2">
                      <p><strong>For faster setup, place images directly in:</strong></p>
                      <code className="block bg-blue-100 p-2 rounded text-xs">
                        D:\Swapnil\Hackathon-2024\cricket-face-mashup-web\uploads\
                      </code>
                      <p>Then click the <strong>"Reload"</strong> button above to auto-detect images!</p>
                    </div>
                  </div>
                </div>
              </div>
            ) : gameState === 'ready' ? (
              <div className="space-y-6">
                <div className="card p-6 text-center bg-gradient-to-br from-green-50 to-blue-50">
                  <div className="space-y-4">
                    <div className="flex items-center justify-center space-x-2">
                      <Zap className="h-6 w-6 text-yellow-500" />
                      <h3 className="text-xl font-semibold text-gray-800">
                        Ready to Play!
                      </h3>
                    </div>
                    <p className="text-gray-600">
                      You have {players.length} cricket players loaded. Start the face mashup challenge!
                    </p>
                    <button
                      onClick={startNewRound}
                      disabled={isLoading}
                      className="btn-primary px-8 py-3 text-lg inline-flex items-center space-x-2"
                    >
                      <Shuffle className="h-5 w-5" />
                      <span>{isLoading ? 'Creating Mashup...' : 'Start First Round'}</span>
                    </button>
                  </div>
                </div>
                
                {/* Show loaded players grid */}
                <div className="card p-6">
                  <h3 className="text-lg font-semibold text-gray-800 mb-4 flex items-center space-x-2">
                    <Users className="h-5 w-5 text-green-500" />
                    <span>Loaded Players ({players.length})</span>
                  </h3>
                  <div className="grid grid-cols-3 md:grid-cols-4 lg:grid-cols-6 gap-4">
                    {players.slice(0, 12).map((player, index) => (
                      <div key={index} className="text-center space-y-2">
                        <div className="aspect-square bg-gray-100 rounded-lg overflow-hidden">
                          <img
                            src={`${API_BASE}${player.image_url}`}
                            alt={player.name}
                            className="w-full h-full object-cover"
                          />
                        </div>
                        <p className="text-xs font-medium text-gray-700 truncate">{player.name}</p>
                      </div>
                    ))}
                  </div>
                  {players.length > 12 && (
                    <p className="text-center text-gray-500 mt-4">
                      ... and {players.length - 12} more players
                    </p>
                  )}
                </div>
              </div>
            ) : (
              <GameSection
                currentMashup={currentMashup}
                quizOptions={quizOptions}
                onAnswer={handleAnswer}
                gameMode={gameMode}
                isLoading={isLoading}
                onNextRound={startNewRound}
              />
            )}
          </div>

          {/* Sidebar */}
          <div className="lg:col-span-1">
            <StatsSection
              score={score}
              totalQuestions={totalQuestions}
              stats={stats}
              playersCount={players.length}
            />
          </div>
        </div>
      </main>

      {/* Footer */}
      <footer className="mt-16 bg-white border-t border-gray-200">
        <div className="max-w-7xl mx-auto px-4 py-8 text-center">
          <div className="space-y-4">
            <div className="flex items-center justify-center space-x-2">
              <div className="w-8 h-8 bg-gradient-to-r from-green-500 to-blue-500 rounded-full flex items-center justify-center">
                <Trophy className="h-4 w-4 text-white" />
              </div>
              <span className="text-lg font-semibold text-gray-800">Cricket Face Mashup</span>
            </div>
            <p className="text-gray-600">
              Powered by AI • Face blending technology • Cricket analytics
            </p>
            <div className="flex items-center justify-center space-x-6 text-sm text-gray-500">
              <div className="flex items-center space-x-1">
                <Upload className="h-4 w-4" />
                <span>Auto-load Images</span>
              </div>
              <div className="flex items-center space-x-1">
                <Zap className="h-4 w-4" />
                <span>AI Processing</span>
              </div>
              <div className="flex items-center space-x-1">
                <Shuffle className="h-4 w-4" />
                <span>Face Blending</span>
              </div>
            </div>
          </div>
        </div>
      </footer>
    </div>
  );
}

export default App;
